import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

// 🔠 Simple Markdown formatter
function formatText(text) {
  if (!text) return '';
  return text
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>') // bold
    .replace(/\*(.*?)\*/g, '<em>$1</em>')             // italic
    .replace(/\n/g, '<br>');                          // line breaks
}

function App() {
  const [theme, setTheme] = useState('');
  const [calendar, setCalendar] = useState([]);
  const [loading, setLoading] = useState(false);
  const [postContent, setPostContent] = useState(null);

  const generateCalendar = async () => {
    if (!theme.trim()) return;
    setLoading(true);
    try {
      const response = await axios.post('http://127.0.0.1:5000/generate', { theme });
      setCalendar(response.data);
    } catch (err) {
      alert('❌ Error generating calendar. Make sure the Flask backend is running.');
    }
    setLoading(false);
  };

  const generatePost = async (item) => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/generate_post', {
        idea: item.post_idea,
        theme: theme
      });
      setPostContent(response.data);
    } catch (err) {
      alert('❌ Error generating post content.');
    }
  };

  return (
    <div className="App">
      <h1>📅 AI Content Calendar Generator</h1>

      <div className="input-group">
        <input
          type="text"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
          placeholder="🎯 Enter a theme (e.g. Chess, Marketing)"
        />
        <button onClick={generateCalendar}>Generate Calendar</button>
      </div>

      {loading && <p className="loading">⏳ Generating calendar...</p>}

      {calendar.length > 0 && (
        <table className="calendar-table">
          <thead>
            <tr>
              <th>#</th>
              <th>Date</th>
              <th>Day</th>
              <th>Theme</th>
              <th>Post Idea</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {calendar.map((item, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{item.date}</td>
                <td>{item.day}</td>
                <td>{item.weekly_theme}</td>
                <td>{item.post_idea}</td>
                <td>
                  <button onClick={() => generatePost(item)} className="action-btn">✨ Generate Post</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {postContent && (
        <div className="modal">
          <div className="modal-content">
            <h2>📝 Ready-to-Post Content</h2>
            <p><strong>📄 Content:</strong><br />
              <span dangerouslySetInnerHTML={{ __html: formatText(postContent.CONTENT) }} />
            </p>
            <p><strong>🏷️ Hashtags:</strong><br />{postContent.HASHTAGS}</p>
            <p><strong>📢 CTA:</strong><br />{postContent.CTA}</p>
            <button onClick={() => setPostContent(null)} className="close-btn">Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
