import React, { useState } from 'react';
import axios from 'axios';
import CalendarCard from './CalendarCard';
import './App.css';

function App() {
  const [theme, setTheme] = useState('');
  const [calendar, setCalendar] = useState([]);
  const [loading, setLoading] = useState(false);

  const generateCalendar = async () => {
    if (!theme.trim()) return;
    setLoading(true);
    try {
      console.log("üì§ Sending request to Flask...");
      const response = await axios.post('http://127.0.0.1:5000/generate', { theme });
      console.log("‚úÖ Response from Flask:", response.data);
      setCalendar(response.data);
    } catch (err) {
      console.error("‚ùå Error from backend:", err);
      alert('Error generating calendar. See console.');
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>üìÖ AI Content Calendar Generator</h1>

      <div className="input-group">
        <input
          type="text"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
          placeholder="Enter a theme (e.g. Cricket)"
        />
        <button onClick={generateCalendar}>Generate</button>
      </div>

      {loading && <p>Generating calendar...</p>}

      <div className="calendar-grid">
  {calendar && calendar.length > 0 ? (
    calendar.map((item, idx) => (
      <CalendarCard key={idx} item={item} />
    ))
  ) : (
    !loading && <p>No calendar to show yet. Try generating one.</p>
  )}
</div>


      {/* DEBUG: Display raw JSON output from Flask */}
      <pre style={{ textAlign: "left", padding: "1rem", background: "#f4f4f4", marginTop: "1rem" }}>
        {JSON.stringify(calendar, null, 2)}
      </pre>
    </div>
  );
}

export default App;
