import React, { useState } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import './App.css';

function App() {
  const [theme, setTheme] = useState('');
  const [calendar, setCalendar] = useState([]);
  const [loading, setLoading] = useState(false);
  const [postContent, setPostContent] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [imagePreview, setImagePreview] = useState('');
  const [businessUnit, setBusinessUnit] = useState('');
  const [posting, setPosting] = useState(false);
  const [scheduleTime, setScheduleTime] = useState('');

  const generateCalendar = async () => {
    if (!theme.trim()) return;
    setLoading(true);
    setPostContent(null);
    try {
      const response = await axios.post('http://127.0.0.1:5000/generate', { theme });
      setCalendar(response.data);
    } catch (err) {
      console.error(err);
      alert('⚠️ Error generating calendar. Is Flask running?');
    }
    setLoading(false);
  };

  const generatePost = async (item) => {
    setPostContent(null);
    try {
      const response = await axios.post('http://127.0.0.1:5000/generate_post', {
        idea: item.post_idea,
        theme: theme
      });
      const content = response.data.CONTENT;
      if (content) {
        setPostContent({ ...response.data, idea: item.post_idea });
        fetchPexelsImage(content); // auto-fetch from caption
      } else {
        alert("Post content is empty.");
      }
    } catch (err) {
      alert('Error generating post content.');
    }
  };

  const fetchPexelsImage = async (caption) => {
    try {
      const res = await axios.get('https://api.pexels.com/v1/search', {
        headers: {
          Authorization: 'T8aIk0CcSbwGnZo07sHvve03LtQOEbJZ284izFJe5dYR6HLogCqpqe1V'
        },
        params: {
          query: caption.split(" ").slice(0, 3).join(" "),
          per_page: 1
        }
      });
      if (res.data.photos.length > 0) {
        const img = res.data.photos[0].src.medium;
        setImageUrl(img);
        setImagePreview(img);
      }
    } catch (e) {
      console.error(e);
      alert("❌ Failed to fetch images from Pexels.");
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result);
      setImageUrl(''); // Do not send local image directly
    };
    reader.readAsDataURL(file);
  };

  const autoPostToInstagram = async () => {
    if (!postContent || (!imageUrl && !imagePreview) || !businessUnit) {
      alert("❗ Please fill in all fields (image or upload, business unit) and generate a post first.");
      return;
    }
    setPosting(true);
    try {
      const payload = {
        theme,
        idea: postContent.idea,
        image_url: imageUrl,
        business_unit: businessUnit
      };

      const response = await axios.post('http://127.0.0.1:5000/auto_post', payload);
      alert(response.data.message || '⚠️ Post failed');
    } catch (error) {
      alert('❌ Error posting to Instagram.');
    }
    setPosting(false);
  };

  const schedulePostToInstagram = async () => {
    if (!postContent || (!imageUrl && !imagePreview) || !businessUnit || !scheduleTime) {
      alert("❗ Please fill in all fields including schedule time.");
      return;
    }
    setPosting(true);
    try {
      const payload = {
        theme,
        idea: postContent.idea,
        image_url: imageUrl,
        business_unit: businessUnit,
        schedule_time: scheduleTime
      };

      const response = await axios.post('http://127.0.0.1:5000/schedule_post', payload);
      alert(response.data.message || '⚠️ Scheduling failed');
    } catch (error) {
      alert('❌ Error scheduling the post.');
    }
    setPosting(false);
  };

  return (
    <div className="App">
      <h1>📅 AI Content Calendar Generator + 📤 Instagram Auto Post</h1>

      <div className="input-group">
        <input
          type="text"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
          placeholder="Enter a theme (e.g. Football)"
        />
        <button onClick={generateCalendar}>Generate Calendar</button>
      </div>

      {loading && <p>⏳ Generating calendar...</p>}

      {calendar.length > 0 && (
        <table className="calendar-table">
          <thead>
            <tr>
              <th>#</th>
              <th>Date</th>
              <th>Day</th>
              <th>Theme</th>
              <th>Post Idea</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {calendar.map((item, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{item.date}</td>
                <td>{item.day}</td>
                <td>{item.weekly_theme}</td>
                <td style={{ whiteSpace: 'pre-wrap' }}>{item.post_idea}</td>
                <td>
                  <button onClick={() => generatePost(item)}>Generate Post</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {postContent && (
        <div className="modal">
          <h3>📝 Generated Post</h3>
          <p><strong>Content:</strong></p>
          <ReactMarkdown>{postContent.CONTENT}</ReactMarkdown>

          <p><strong>Hashtags:</strong></p>
          <ReactMarkdown>{postContent.HASHTAGS}</ReactMarkdown>

          <p><strong>CTA:</strong></p>
          <ReactMarkdown>{postContent.CTA}</ReactMarkdown>

          <hr />

          {imagePreview && <img src={imagePreview} alt="Preview" style={{ width: "100%", maxHeight: 300, objectFit: "cover", borderRadius: "8px", marginBottom: "10px" }} />}

          <input
            type="text"
            value={imageUrl}
            onChange={(e) => {
              setImageUrl(e.target.value);
              setImagePreview(e.target.value);
            }}
            placeholder="Enter Image URL or upload below"
            style={{ width: '100%', marginBottom: '10px' }}
          />

          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            style={{ marginBottom: '10px' }}
          />

          <input
            type="text"
            value={businessUnit}
            onChange={(e) => setBusinessUnit(e.target.value)}
            placeholder="Enter Business Unit (e.g., YourBrand)"
            style={{ width: '100%', marginBottom: '10px' }}
          />

          <input
            type="datetime-local"
            value={scheduleTime}
            onChange={(e) => setScheduleTime(e.target.value)}
            style={{ width: '100%', marginBottom: '10px' }}
          />

          <button onClick={autoPostToInstagram} disabled={posting}>
            {posting ? "📤 Posting..." : "📤 Post to Instagram Now"}
          </button>

          <button onClick={schedulePostToInstagram} disabled={posting} style={{ marginLeft: '10px' }}>
            {posting ? "📆 Scheduling..." : "📆 Schedule Post"}
          </button>

          <br /><br />
          <button onClick={() => setPostContent(null)}>Close</button>
        </div>
      )}
    </div>
  );
}

export default App;
