import React, { useState } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import './App.css';

function App() {
  const [theme, setTheme] = useState('');
  const [calendar, setCalendar] = useState([]);
  const [loading, setLoading] = useState(false);
  const [postContent, setPostContent] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [businessUnit, setBusinessUnit] = useState('');
  const [posting, setPosting] = useState(false);
  const [scheduleTime, setScheduleTime] = useState('');

  const generateCalendar = async () => {
    if (!theme.trim()) return;
    setLoading(true);
    setPostContent(null);
    try {
      const response = await axios.post('http://127.0.0.1:5000/generate', { theme });
      setCalendar(response.data);
    } catch (err) {
      console.error(err);
      alert('‚ö†Ô∏è Error generating calendar. Is Flask running?');
    }
    setLoading(false);
  };

  const generatePost = async (item) => {
    setPostContent(null);
    try {
      const response = await axios.post('http://127.0.0.1:5000/generate_post', {
        idea: item.post_idea,
        theme: theme
      });
      if (response.data && response.data.CONTENT) {
        setPostContent({
          ...response.data,
          idea: item.post_idea
        });
      } else {
        alert("Post content is empty.");
      }
    } catch (err) {
      alert('Error generating post content.');
    }
  };

  const autoPostToInstagram = async () => {
    if (!postContent || !imageUrl || !businessUnit) {
      alert("‚ùó Please fill in all fields (image URL, business unit) and generate a post first.");
      return;
    }
    setPosting(true);
    try {
      const payload = {
        theme,
        idea: postContent.idea,
        image_url: imageUrl,
        business_unit: businessUnit
      };

      const response = await axios.post('http://127.0.0.1:5000/auto_post', payload);
      if (response.data.message) {
        alert(response.data.message);
      } else {
        alert('‚ö†Ô∏è Post failed: ' + JSON.stringify(response.data));
      }
    } catch (error) {
      alert('‚ùå Error posting to Instagram.');
      console.error(error);
    }
    setPosting(false);
  };

  const schedulePostToInstagram = async () => {
    if (!postContent || !imageUrl || !businessUnit || !scheduleTime) {
      alert("‚ùó Please fill in all fields including schedule time.");
      return;
    }
    setPosting(true);
    try {
      const payload = {
        theme,
        idea: postContent.idea,
        image_url: imageUrl,
        business_unit: businessUnit,
        schedule_time: scheduleTime
      };

      const response = await axios.post('http://127.0.0.1:5000/schedule_post', payload);
      if (response.data.message) {
        alert(response.data.message);
      } else {
        alert('‚ö†Ô∏è Scheduling failed: ' + JSON.stringify(response.data));
      }
    } catch (error) {
      alert('‚ùå Error scheduling the post.');
      console.error(error);
    }
    setPosting(false);
  };

  return (
    <div className="App">
      <h1>üìÖ AI Content Calendar Generator + üì§ Instagram Auto Post</h1>

      <div className="input-group">
        <input
          type="text"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
          placeholder="Enter a theme (e.g. Football)"
        />
        <button onClick={generateCalendar}>Generate Calendar</button>
      </div>

      {loading && <p>‚è≥ Generating calendar...</p>}

      {calendar.length > 0 && (
        <table className="calendar-table">
          <thead>
            <tr>
              <th>#</th>
              <th>Date</th>
              <th>Day</th>
              <th>Theme</th>
              <th>Post Idea</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {calendar.map((item, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{item.date}</td>
                <td>{item.day}</td>
                <td>{item.weekly_theme}</td>
                <td style={{ whiteSpace: 'pre-wrap' }}>{item.post_idea}</td>
                <td>
                  <button onClick={() => generatePost(item)}>Generate Post</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {postContent && (
        <div className="modal">
          <h3>üìù Generated Post</h3>
          <p><strong>Content:</strong></p>
          <ReactMarkdown>{postContent.CONTENT}</ReactMarkdown>

          <p><strong>Hashtags:</strong></p>
          <ReactMarkdown>{postContent.HASHTAGS}</ReactMarkdown>

          <p><strong>CTA:</strong></p>
          <ReactMarkdown>{postContent.CTA}</ReactMarkdown>

          <hr />

          <input
            type="text"
            value={imageUrl}
            onChange={(e) => setImageUrl(e.target.value)}
            placeholder="Enter Image URL"
            style={{ width: '100%', marginBottom: '10px' }}
          />

          <input
            type="text"
            value={businessUnit}
            onChange={(e) => setBusinessUnit(e.target.value)}
            placeholder="Enter Business Unit (e.g., YourBrand)"
            style={{ width: '100%', marginBottom: '10px' }}
          />

          <input
            type="datetime-local"
            value={scheduleTime}
            onChange={(e) => setScheduleTime(e.target.value)}
            style={{ width: '100%', marginBottom: '10px' }}
          />

          <button onClick={autoPostToInstagram} disabled={posting}>
            {posting ? "üì§ Posting..." : "üì§ Post to Instagram Now"}
          </button>

          <button onClick={schedulePostToInstagram} disabled={posting} style={{ marginLeft: '10px' }}>
            {posting ? "üìÜ Scheduling..." : "üìÜ Schedule Post"}
          </button>

          <br /><br />
          <button onClick={() => setPostContent(null)}>Close</button>
        </div>
      )}
    </div>
  );
}

export default App;
