import React, { useState } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import './App.css';

function App() {
  const [theme, setTheme] = useState('');
  const [calendar, setCalendar] = useState([]);
  const [loading, setLoading] = useState(false);

  const [postContent, setPostContent] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [uploadedImage, setUploadedImage] = useState(null);
  const [pexelsImages, setPexelsImages] = useState([]);

  const [businessUnit, setBusinessUnit] = useState('');
  const [scheduleTime, setScheduleTime] = useState('');
  const [posting, setPosting] = useState(false);

  const generateCalendar = async () => {
    if (!theme.trim()) return;
    setLoading(true);
    setPostContent(null);
    try {
      const res = await axios.post('http://127.0.0.1:5000/generate', { theme });
      setCalendar(res.data);
    } catch (e) {
      console.error(e);
      alert('⚠️ Error generating calendar. Is Flask running?');
    }
    setLoading(false);
  };

  const generatePost = async (item) => {
    setPostContent(null);
    setImageUrl('');
    setUploadedImage(null);
    setPexelsImages([]);

    try {
      const res = await axios.post('http://127.0.0.1:5000/generate_post', {
        idea: item.post_idea,
        theme,
      });
      if (res.data.CONTENT) {
        setPostContent({ ...res.data, idea: item.post_idea });
        fetchPexelsImages(res.data.CONTENT);
      }
    } catch (e) {
      console.error(e);
      alert('⚠️ Post generation failed');
    }
  };

  const fetchPexelsImages = async (caption) => {
    try {
      const res = await axios.post('http://127.0.0.1:5000/fetch_images', {
        query: caption,
      });
      setPexelsImages(res.data.photos || []);
    } catch (e) {
      console.error(e);
      alert('❌ Failed to fetch images from Pexels.');
    }
  };

  const handleUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const preview = URL.createObjectURL(file);
    setUploadedImage({ file, preview });
    setImageUrl(preview);
    setPexelsImages([]);
  };

  const sendRequest = async (endpoint) => {
    if (!postContent?.idea || !imageUrl || !businessUnit) {
      return alert('❗ Please generate post, select/upload an image & fill Business Unit.');
    }
    if (endpoint === '/schedule_post' && !scheduleTime) {
      return alert('❗ Please set a schedule date & time.');
    }

    setPosting(true);
    const payload = {
      theme,
      idea: postContent.idea,
      image_url: imageUrl,
      business_unit: businessUnit,
      ...(endpoint === '/schedule_post' && { schedule_time: scheduleTime }),
    };

    try {
      const res = await axios.post(`http://127.0.0.1:5000${endpoint}`, payload);
      alert(res.data.message || '✅ Success');
    } catch (e) {
      console.error(e);
      alert('❌ An error occurred');
    }
    setPosting(false);
  };

  return (
    <div className="App">
      <h1>📅 AI Calendar + 📤 Insta Auto & Schedule</h1>
      <div className="input-group">
        <input
          value={theme}
          onChange={e => setTheme(e.target.value)}
          placeholder="Enter a theme"
        />
        <button onClick={generateCalendar}>Generate Calendar</button>
      </div>

      {loading && <p>⏳ Generating calendar…</p>}

      {calendar.length > 0 && (
        <table className="calendar-table">
          <thead>
            <tr><th>#</th><th>Date</th><th>Day</th><th>Theme</th><th>Idea</th><th>Generate</th></tr>
          </thead>
          <tbody>
            {calendar.map((item, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{item.date}</td>
                <td>{item.day}</td>
                <td>{item.weekly_theme}</td>
                <td style={{ whiteSpace: 'pre-wrap' }}>{item.post_idea}</td>
                <td><button onClick={() => generatePost(item)}>Generate</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {postContent && (
        <div className="modal">
          <h3>📝 Generated Post</h3>
          <ReactMarkdown>{postContent.CONTENT}</ReactMarkdown>
          <ReactMarkdown>{postContent.HASHTAGS}</ReactMarkdown>
          <ReactMarkdown>{postContent.CTA}</ReactMarkdown>
          <hr />

          {imageUrl && (
            <img src={imageUrl} alt="Preview" style={{ maxWidth: '100%', borderRadius: 6, marginBottom: 12 }} />
          )}

          <button onClick={() => fetchPexelsImages(postContent.CONTENT)}>📸 Fetch Pexels</button>
          <input type="file" accept="image/*" onChange={handleUpload} style={{ marginTop: 10 }} />

          {pexelsImages.length > 0 && (
            <div className="pexels-grid">
              {pexelsImages.map((img, i) => (
                <img
                  key={i}
                  src={img.src.medium}
                  alt=""
                  className={imageUrl === img.src.original ? 'selected' : ''}
                  onClick={() => setImageUrl(img.src.original)}
                />
              ))}
            </div>
          )}

          <input
            value={businessUnit}
            onChange={e => setBusinessUnit(e.target.value)}
            placeholder="Business Unit"
            style={{ width: '100%', marginTop: 12 }}
          />

          <input
            type="datetime-local"
            value={scheduleTime}
            onChange={e => setScheduleTime(e.target.value)}
            style={{ width: '100%', marginTop: 12 }}
          />

          <button onClick={() => sendRequest('/auto_post')} disabled={posting}>
            {posting ? 'Posting…' : 'Post Now'}
          </button>
          <button
            onClick={() => sendRequest('/schedule_post')}
            disabled={posting}
            style={{ marginLeft: 10 }}
          >
            {posting ? 'Scheduling…' : 'Schedule Post'}
          </button>

          <button onClick={() => setPostContent(null)} style={{ marginTop: 10 }}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
