import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [theme, setTheme] = useState('');
  const [calendar, setCalendar] = useState([]);
  const [loading, setLoading] = useState(false);

  const generateCalendar = async () => {
    if (!theme.trim()) return;
    setLoading(true);
    try {
      const response = await axios.post('http://127.0.0.1:5000/generate', { theme });
      console.log("ðŸ“¦ Response:", response.data);
      setCalendar(response.data);
    } catch (err) {
      alert('Error generating calendar. Is Flask running?');
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>ðŸ“… AI Content Calendar Generator</h1>
      <div className="input-group">
        <input
          type="text"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
          placeholder="Enter a theme (e.g. Cricket)"
        />
        <button onClick={generateCalendar}>Generate</button>
      </div>

      {loading && <p>Generating...</p>}

      {calendar.length > 0 && (
        <table className="calendar-table">
          <thead>
            <tr>
              <th>#</th>
              <th>Date</th>
              <th>Day</th>
              <th>Theme</th>
              <th>Post Idea</th>
              <th>Content</th>
              <th>Hashtags</th>
              <th>CTA</th>
            </tr>
          </thead>
          <tbody>
            {calendar.map((item, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{item.date}</td>
                <td>{item.day}</td>
                <td>{item.weekly_theme}</td>
                <td>{item.post_idea}</td>
                <td>{item.content}</td>
                <td>{item.hashtags}</td>
                <td>{item.cta}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
